import {useState} from "react";

const Forecast = (props) => {
    const[selectedLocation,setSelectedLocation]=useState({name: "0 is selected"});

    const selectLocationsList = props.locations.map((location) =>
        <option key={location.name.toString()} value={location.name} onClick={()=>setSelectedLocation(location)}>{location.name} </option>
    );
    console.log(props.locations.length);
    return (
        <>
            <h2>Forecast</h2>
            <select className="form-select" size="15" multiple aria-label="multiple select example">
                {props.locations.length > 0 ? <>
                        <option selected>Select a location from the list</option>
                        {selectLocationsList} </> :
                    <option selected onClick={()=>setSelectedLocation({name: "0 is selected"})}>No locations yet. Go to the Locations tab to add a location</option>}

            </select>
            <button type="submit" className="btn btn-primary">Show Forecast</button>
            <div> {selectedLocation.name!=="0 is selected" ? <p><h6>{selectedLocation.name}</h6> {selectedLocation.latitude} {' '} {selectedLocation.longitude}</p> : <></>}</div>

        </>);
}

export default Forecast;
