const Forecast = (props) => {

    const selectLocationsList = props.locations.map((location) =>
        <option key={location.name.toString()} value={location.name}>{location.name}</option>

    );

    return (
        <>
            <h2>Forecast</h2>
    <select className="form-select" size="15" multiple aria-label="multiple select example">
        {props.locations.length > 0 ? selectLocationsList : <p>no locations yet</p>}
    </select>
            </> );
}

export default Forecast;
