import {useState} from "react";

const DisplaysForecastResults =(props)=>{
    const [{ data, isLoading, isError }, doFetch] = useDataApi(
        'https://hn.algolia.com/api/v1/search?query=redux',
        { hits: [] },
    );


    return(
        <>
    <h3>{props.theLocation.name}</h3>
    <img src= {`https:www.7timer.info/bin/astro.php?%20lon=${props.theLocation.longitude}&lat=${props.theLocation.latitude}&ac=0&lang=en&unit=metric&output=internal&tzshift=0`} />
            <>

                <form
                    onSubmit={event => {
                        doFetch(
                            `http://hn.algolia.com/api/v1/search?query=${query}`,[]
                        );
                        event.preventDefault();
                    }}
                >
                    <input type="text" className="form-control" value={query} onChange={event => setQuery(event.target.value)}/>
                    <button className="btn btn-primary" type="submit">Search</button>
                </form>

                {isError && <div>Something went wrong ...</div>}

                {isLoading ? (
                    <div className="alert alert-warning">Loading ...</div>
                ) : (
                    <ol>
                        {data.hits.map(item => (
                            <li key={item.objectID}>
                                <a href={item.url}>{item.title}</a>
                            </li>
                        ))}
                    </ol>
                )}
            </>
</> );
};

export default DisplaysForecastResults;
