import {useEffect, useState} from "react";
import axios from 'axios';

const listDayForecast=(dataList)=>{
    return(
        <ul className="list-group">
            dataList.map((dayFOrecast)=>
            <li className="list-group-item d-flex justify-content-between align-items-center">
                Weather: {dayFOrecast.weather}<br></br>
                Temperatures: {dayFOrecast.temp2m.min} " - " {day.temp2m.max}<br></br>
                Wind: {dayFOrecast.wind10m_max}<br></br>
                <span className="badge bg-primary rounded-pill">{dayFOrecast.date}</span>
            </li>
            )
        </ul>
        );
}

const useDataApi = (theUrl) => {
    const [data, setData] = useState({null: "hhhh"}); // data to be fetched
    const [isLoading, setIsLoading] = useState(false); // is it fetching?
    const [error, setError] = useState({isError: false, msg: ""}); // is there an error?
    useEffect( () => {
    const fetchData = async () => {
        setError(error=>({...error,isError: false})); // reset error state
        setIsLoading(true); // set loading state to true to show loading indicator for example
        try {
            const result = await axios(theUrl);
            setData(result.data);
        } catch (err) {
            console.log("ffffffffffffffffffffffffffffffffffffffff");
            setError({isError: true, msg: err.message}); // an error occurred, set error state to true
        }
        finally {
            setIsLoading(false); // set loading state to false to hide loading indicator
        }
    };
    fetchData(); // execute the function above
    }, []);
  return {data, isLoading, error}; // return the data and the URL setter function
};

const DisplaysForecastResults = (props) => {
    const {data, isLoading, error} = useDataApi(`https://www.7timer.info/bin/api.pl?lon=${props.theLocation.longitude}&lat=${props.theLocation.latitude}&product=civillight&output=json`);
    console.log(data);

    return (
        <>
            <h3>{props.theLocation.name}</h3>
            <img src={`https:www.7timer.info/bin/astro.php?%20lon=${props.theLocation.longitude}&lat=${props.theLocation.latitude}&ac=0&lang=en&unit=metric&output=internal&tzshift=0`}/>
            <>

                {/*{error.isError && <div>Something went wrong ...</div>}*/}
                {isLoading ? (
                    <div className="alert alert-warning">Loading ...</div>
                ) : (<p>{error.isError? "Something went wrong: "+error.msg : listDayForecast(data.dataseries)}</p>)

                    //  </ol>
                }
            </>
        </>);
};
export default DisplaysForecastResults;

