import {useState} from "react";
import DisplaysForecastResults from "./DisplaysForecastResults";


const Forecast = (props) => {
    const [selectedLocation,setSelectedLocation]=useState({});

const handleShowForecast=(event)=>{
    document.getElementById("DisplaysForecast").classList.add("d-block");
}
    const selectLocationsList = props.locations.map((location) =>
        <option key={location.name.toString()} value={location.name} onClick={()=>setSelectedLocation(location)}>{location.name} </option>
    );


    return (
        <>
            <h2>Forecast:</h2>
            <DisplaysForecastResults className={'d-none'} id="DisplaysForecast" theLocation={selectedLocation}></DisplaysForecastResults>


            <select className="form-select" size="8" multiple aria-label="multiple select example">
                {props.locations.length > 0 ? <>
                        <option selected onClick={()=>setSelectedLocation({})}>Select a location from the list</option>
                        {selectLocationsList} </> :
                    <option>No locations yet. Go to the Locations tab to add a location</option>}
            </select>
            <button className="btn btn-primary" onClick={handleShowForecast}>Show Forecast</button>
            <div> {selectedLocation? <p><h6>{selectedLocation.name}</h6> {selectedLocation.latitude} {' '} {selectedLocation.longitude}</p> : ""}</div>
        </>);
}

export default Forecast;
