import {Outlet} from "react-router";
import {useState} from "react";

export default function AddLocationForm(props) {
   // const locationsList=props.locations.locationsList;
    const [inputs, setInputs] = useState({})
function handleAddLocation(event){
    event.preventDefault();
    props.locations.setLocations([...props.locations.locationsList,{name:event.navalue,latitude:event.latitude.value,longitude:event.longitude.value}]);
}
    return (
        <form className="border p-3" onSubmit={handleAddLocation}>
            <h2>Add Location:</h2>
            <div className="mb-3 col">
                <label htmlFor="nameInput" className="form-label">Name:</label>
                <input type="text" className="form-control" id="nameInput" name="name" value={inputs.name || ""}
                />
            </div>
            <div className="mb-3 col">
                <label htmlFor="latitudeInput" className="form-label">Latitude:</label>
                <input type="double" className="form-control latitude" id="latitudeInput" name="latitude"/>
            </div>
            <div className="mb-3 col">
                <label htmlFor="longitudeInput" className="form-label">Longitude:</label>
                <input type="number" className="form-control" id="longitudeInput" name="longitude"/>
            </div>
            <button type="submit" className="btn btn-primary">Add Location</button>
        </form>
    );
}
