const Forecast = (props) => {

    const selectLocationsList = () => {
        return (
            <select className="form-select" size="15" multiple aria-label="multiple select example">

                    props.locations.map((location)=>
                    <option value={location.name}>{location.name}</option>
                    );

            </select>)

    }
    return (
        <>
            <h2>Forecast</h2>
            {props.locations.length > 0 ? selectLocationsList() : <p>no locations yet</p>}

        </>);
}

export default Forecast;
