import {Outlet} from "react-router";
import {useState} from "react";

export default function AddLocationForm(props) {
    // const locationsList=props.locations.locationsList;
    const [inputs, setInputs] = useState({})
    const [validationInputs, setValidationInputs] = useState({name:{isValid:true,errorMessage:"gggggggggggggggggggggggg"}})
    const [inputsArrValid, setInputsArrValid] = useState(false);

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }
    useEffect(() => {
        if (false) { // if the input is less than 5 characters or contains a number
            setInputsArrValid(false);
        } else {
            setInputsArrValid(true);
        }
    }, [inputs]);
    const handleAddLocation = (event) => {
        event.preventDefault();
        props.locations.setLocations([...props.locations.locationsList, {
            name: inputs.name,
            latitude: inputs.latitude,
            longitude: inputs.longitude
        }]);
    }
    return (
        <form className="border p-3" onSubmit={handleAddLocation}>
            <h2>Add Location:</h2>
            <div className="mb-3 col">
                <label htmlFor="nameInput" className="form-label">Name:</label>
                <input type="text" className="form-control" id="nameInput" name="name" value={inputs.name || ""}
                       onChange={handleChange}/>
                <p  className={validationInputs.name.isValid ? "alert alert-info" : "alert alert-danger"}> {validationInputs.name.isValid ? "" : validationInputs.name.errorMessage}</p>
            </div>
            <div className="mb-3 col">
                <label htmlFor="latitudeInput" className="form-label">Latitude:</label>
                <input type="double" className="form-control latitude" id="latitudeInput" name="latitude"
                       value={inputs.latitude || ""}
                       onChange={handleChange}/>
            </div>
            <div className="mb-3 col">
                <label htmlFor="longitudeInput" className="form-label">Longitude:</label>
                <input type="number" className="form-control" id="longitudeInput" name="longitude"
                       value={inputs.longitude || ""}
                       onChange={handleChange}/>
            </div>
            <button type="submit" className="btn btn-primary">Add Location</button>
        </form>
    );
}
