import {Outlet} from "react-router";

export default function AddLocationForm(props) {
    const listLocations=props.locations.locationsList;
function handleAddLocation(event){
    return [...props.locations.locationsList,{name:event.name.value,latitude:event.latitude.value,longitude:event.longitude.value}]
}
    return (
        <form className="border p-3">
            <h2>Add Location:</h2>
            <div className="mb-3 col">
                <label htmlFor="nameInput" className="form-label">Name:</label>
                <input type="text" className="form-control" id="nameInput" name="name"/>
            </div>
            <div className="mb-3 col">
                <label htmlFor="latitudeInput" className="form-label">Latitude:</label>
                <input type="double" className="form-control" id="latitudeInput"/>
            </div>
            <div className="mb-3 col">
                <label htmlFor="longitudeInput" className="form-label">Longitude:</label>
                <input type="number" className="form-control" id="longitudeInput"/>
            </div>
            <button type="submit" className="btn btn-primary" onSubmit={handleAddLocation}>Add Location</button>
        </form>
    );
}
